import { useState, useEffect } from 'react';
import React from 'react'
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

export default function MyOrder() {

    const [orderData, setorderData] = useState({})
    const fetchMyOrder = async () => {
        console.log(localStorage.getItem('userEmail'))
        await fetch("http://localhost:4000/api/MyOrder_data", {
            // credentials: 'include',
            // Origin:"http://localhost:3000/login",
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                email: localStorage.getItem('userEmail')
            })
        }).then(async (res) => {
            let response = await res.json()
            await setorderData(response)
        })



        // await res.map((data)=>{
        //    console.log(data)
        // })


    }

    useEffect(() => {
        fetchMyOrder()
    }, [])


    return (
        <>
            <div>
                <Navbar></Navbar>
            </div>

            <div className='container'>
                <div className='row'>
                    {orderData !== {} ? (
                        Array(orderData).map((data) => {
                            return data.orderData ? (
                                data.orderData.order_data.slice(0).reverse().map((item, index) => {
                                    // Check if item is an array before using map
                                    if (Array.isArray(item)) {
                                        return item.map((arrayData) => {
                                            return (
                                                <div key={index}>
                                                    {/* ... (unchanged code) */}
                                                </div>
                                            );
                                        });
                                    } else {
                                        // Handle the case where item is not an array
                                        console.error(`Item at index ${index} is not an array.`);
                                        return null;
                                    }
                                })
                            ) : null;
                        })
                    ) : (
                        ""
                    )}
                </div>
            </div>
            <div>
                <Footer></Footer>
            </div>

        </>
    )
}
